//Import Excel API dependency
import org.apache.poi.ss.usermodel.*
import org.apache.poi.xssf.usermodel.*
import org.apache.poi.ss.usermodel.DataFormatter
import org.apache.poi.hssf.usermodel.*
import org.apache.poi.ss.util.*
import org.apache.commons.io.*


def testCase = context.testCase
def filePath = "C:\\Users\\DIS584\\dataSet.xlsx"
def counter

FileInputStream inFile = new FileInputStream(new File(filePath))

//Create Workbook instance holding reference to .xlsx file
XSSFWorkbook workbook = new XSSFWorkbook(inFile)
XSSFSheet sheet = workbook.getSheetAt(0)


//First part of if statement is needed when there is no TotalRow property. Second part of if statement is needed when there is already the property but it's empty.
if(!testCase.hasProperty("TotalRow") || testCase.getPropertyValue("TotalRow").toString()=="") {
 testCase.setPropertyValue("Counter", "0")
}
counter = testCase.getPropertyValue("Counter").toInteger()

//First part of if statement is needed when there is no TotalRow property. Second part of if statement is needed when there is already the property but it's empty.
if (!testCase.hasProperty("TotalRow") || testCase.getPropertyValue("TotalRow").toString()=="") {
    testCase.setPropertyValue("TotalRow", sheet.getPhysicalNumberOfRows().toString())
}

//Read Excel data
//Cell cell = sheet.getCell(0, counter)   //In JXL: getCell(columnNum, rowNum)    //Apache POI is row based.
Row row = sheet.getRow(counter)
Cell cell = row.getCell(0)                //getCell(columnNum)

counter++

inFile.close()

//Copy the Excel test data to properties in Properties test step
testCase.setPropertyValue("Country", cell.getStringCellValue())
testCase.setPropertyValue("Counter", counter.toString())
if (counter == testCase.getPropertyValue("TotalRow").toInteger())	
    testCase.setPropertyValue("isEnd", "True")
else
 testCase.setPropertyValue("isEnd", "False")

